# errors thrown by client.py when something goes wrongclass OpenTTDError(Exception):    passclass ProtocolError(OpenTTDError):    passclass UnexpectedResponse(ProtocolError):    def __init__(to, response):        self.to = to        self.response = response        Exception.__init__(self, "unexpected response to %s: %s")class WrongVersion(ProtocolError):    def __init__(to, unsupported, supported):        self.supported = supported        self.unsupported = unsupported        self.to = to        Exception.__init__(self, "unsupported version in response to %s: %d, supported: %d" % (to, unsupported, supported))class ConnectionError(OpenTTDError):    passclass HTTPError(OpenTTDError):    pass